import express, { json } from "express";
import "express-async-errors";
import "dotenv/config";
import cors from "cors";
import authRouter from "./routers/authRouter.js";
import credentialsRouter from "./routers/credentialsRouter.js";
import notesRouter from "./routers/notesRouter.js";
import documentsRouter from "./routers/documentRouter.js";
import networkRouter from "./routers/networkRouter.js";
import { handleError } from "./middlewares/errorHandler.js";
var app = express();
app.use(json());
app.use(cors());
app.use(authRouter);
app.use(credentialsRouter);
app.use(notesRouter);
app.use(documentsRouter);
app.use(networkRouter);
app.use(handleError);
var PORT = Number(process.env.PORT) || 4000;
app.listen(PORT, function () {
    return console.log("Server up and running on PORT ".concat(PORT, "@").concat(Date()));
});
